import nltk, re
from nltk.corpus import stopwords
from nltk.stem import SnowballStemmer
from django.http.response import JsonResponse
from django.conf import settings
from django.http import JsonResponse, HttpResponse
from rest_framework.decorators import api_view
import os
nltk.download('punkt')
nltk.download('stopwords')


def ngram_tokenize(data, n):
    
    ret = [""] * len(data)
    for i in range(len(data)):
        tmp_data = data[i]
        # Strip punctuation
        stop_words = set(stopwords.words('english')) 

        tmp_data = re.split(r'[^a-zA-Z]+',tmp_data)

        # Lowercase all of the words
        tmp_data = map(lambda word: word.lower(), tmp_data)

        # Stem all of the words:
        stemmer = SnowballStemmer('english')
        tmp_data = list(map(stemmer.stem, tmp_data))

        # Delete stop words
        tmp_data = list(filter(lambda word: word not in stop_words, tmp_data))

        # Delete any empty string 
        tmp_data = [word for word in tmp_data if word]

        # ngram
        ret[i] = tmp_data if n == 1 else ngram(tmp_data, n)
        
    return ret


def ngram(data, n):
    return [" ".join(data[j:j+n]) for j in range(len(data)-n+1)]


def sentence_tokenize(sentences):
    tokenized_sentences = ngram_tokenize(sentences, 1)
    for n in range(2, 4):
        tmp = ngram_tokenize(sentences, n)
        for i in range(len(sentences)):
            tokenized_sentences[i].extend(tmp[i])
    
    return tokenized_sentences
    

