def fact_extractor(sentences, threshold = 0.3):
    token_list = sentence_tokenize(sentences)
    fact_labels = fact_classifier(token_list, threshold)
    return fact_labels


@api_view(["POST"])
def post_facts(request):
	# get sentences
    sentences = request.data["content"]
    print(sentences)
    fact_labels = fact_extractor(sentences)

    facts = {sentences[i]: fact_labels[i] for i in range(len(sentences))}
    return JsonResponse(facts)

